import React from 'react';
import { MenuItem, Select, FormControl, InputLabel } from '@mui/material';
import { styled } from '@mui/system';
import {API_URL_ALL, API_URL_REGION} from './api'
import { useEffect } from 'react'

const FilterContainer = styled('div')(({ theme }) => ({
  display: 'flex',
  justifyContent: 'flex-end',
  marginBottom: theme.spacing(2),
  marginLeft: 'auto', // Pushes the filter to the right
}));


const Filter = ({ selectedRegion, handleRegionChange }) => {
  useEffect(() => {
    const fetchCountryList = async () => {
      try {
        let url = API_URL_ALL;
        if (selectedRegion) {
          url = API_URL_REGION(selectedRegion);
        }
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error('Failed to fetch country data');
        }
        const data = await response.json();

        // Log flag dimensions for each country
        data.forEach((country) => {
          const flagImage = new Image();
          flagImage.src = country.flags.svg;
          flagImage.onload = () => {
            console.log(
              `${country.name.common}: ${flagImage.width}px x ${flagImage.height}px`
            );
          };
        });


      } catch (error) {
        console.error(error);
      }
    };

    fetchCountryList();
  }, [selectedRegion]);

  return (
    <FilterContainer>
      <FormControl sx={{ pr: 2 }}>
        <InputLabel id="region-select-label">Filter by Region</InputLabel>
        <Select
          labelId="region-select-label"
          id="region-select"
          value={selectedRegion}
          onChange={handleRegionChange}
          label="Filter by Region"
          sx={{ minWidth: 200 }}
        >
          <MenuItem value="">All</MenuItem>
          <MenuItem value="africa">Africa</MenuItem>
          <MenuItem value="americas">Americas</MenuItem>
          <MenuItem value="asia">Asia</MenuItem>
          <MenuItem value="europe">Europe</MenuItem>
          <MenuItem value="oceania">Oceania</MenuItem>
        </Select>
      </FormControl>
    </FilterContainer>
  );
};

export default Filter;
