import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import { styled } from '@mui/system';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import InputLabel from '@mui/material/InputLabel';

export const API_URL_ALL = 'https://restcountries.com/v3.1/all';
export const API_URL_NAME = (name) => `https://restcountries.com/v3.1/name/${name}`;
export const API_URL_REGION = (region) => `https://restcountries.com/v3.1/region/${region}`;
export const API_URL_CODE = (borders) => `https://restcountries.com/v3.1/alpha?codes=${borders.join(',')}`;

const CountryCard = styled(Card)(({ theme }) => ({
  borderRadius: theme.spacing(2),
  boxShadow: theme.shadows[3],
  marginBottom: theme.spacing(4),
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-between',
  height: '100%',
}));

const FlagImage = styled('div')(({ imageUrl }) => ({
  width: '100%',
  paddingBottom: '56.25%', // 16:9 aspect ratio (adjust as needed)
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  backgroundImage: `url(${imageUrl})`,
}));

const CountryList = () => {
  const [countryList, setCountryList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedRegion, setSelectedRegion] = useState('');

  const handleRegionChange = (event) => {
    setSelectedRegion(event.target.value);
  };

  useEffect(() => {
    const fetchCountryList = async () => {
      try {
        let url = API_URL_ALL;
        if (selectedRegion) {
          url = API_URL_REGION(selectedRegion);
        }
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error('Failed to fetch country data');
        }
        const data = await response.json();

        // Log flag dimensions for each country
        data.forEach((country) => {
          const flagImage = new Image();
          flagImage.src = country.flags.svg;
          flagImage.onload = () => {
            console.log(
              `${country.name.common}: ${flagImage.width}px x ${flagImage.height}px`
            );
          };
        });

        setCountryList(data);
        setLoading(false);
      } catch (error) {
        console.error(error);
      }
    };

    fetchCountryList();
  }, [selectedRegion]);

  return (
    <Box py={4} px={2} maxWidth="1200px" mx="auto">
      <Grid container spacing={4}>
        <Grid item xs={12} md={6} lg={9} display="flex" justifyContent="flex-end">
          <Box mb={2}>
            <InputLabel id="region-select-label" style={{ color: 'white' }}>
              Filter by Region
            </InputLabel>
            <Select
              labelId="region-select-label"
              id="region-select"
              value={selectedRegion}
              onChange={handleRegionChange}
              style={{ color: 'white', minWidth: '200px' }}
            >
              <MenuItem value="">All</MenuItem>
              <MenuItem value="africa">Africa</MenuItem>
              <MenuItem value="americas">Americas</MenuItem>
              <MenuItem value="asia">Asia</MenuItem>
              <MenuItem value="europe">Europe</MenuItem>
              <MenuItem value="oceania">Oceania</MenuItem>
            </Select>
          </Box>
        </Grid>
        {loading ? (
          <Typography variant="h5" align="center">
            Loading...
          </Typography>
        ) : (
          countryList
            .filter(
              (country) =>
                selectedRegion === '' ||
                country.region.toLowerCase() === selectedRegion
            )
            .map((country, index) => (
              <Grid item key={country.name.common} xs={12} sm={6} md={4} lg={3}>
                <Link to={`/country/${country.cca3}`}>
                  <CountryCard>
                    <FlagImage imageUrl={country.flags.svg} />
                    <CardContent>
                      <Typography variant="h6" component="h2" gutterBottom>
                        {country.name.common}
                      </Typography>
                      <Typography>
                        <strong>Population:</strong> {country.population}
                      </Typography>
                      <Typography>
                        <strong>Region:</strong> {country.region}
                      </Typography>
                      <Typography>
                        <strong>Capital:</strong> {country.capital}
                      </Typography>
                    </CardContent>
                  </CountryCard>
                </Link>
              </Grid>
            ))
        )}
      </Grid>
    </Box>
  );
};

export default CountryList;
